example.code:
[MqttSubscribe("default", "machines/+/heartbeat", QualityOfService.AtMostOnce)]
public class HeartbeatHandler : IMessageHandler<Heartbeat>
{
    public Task HandleAsync(Heartbeat message, IMessageContext context, CancellationToken ct)
    {
        // Handle the heartbeat message
        return Task.CompletedTask;
    }
}

member.example:
// With IServiceProvider (works with any container)
builder.WithHandlerActivator(new ServiceProviderActivator(serviceProvider));

// With custom factory function
builder.WithHandlerActivator(new FuncActivator(type => container.Resolve(type)));
